describe("inflector",function(){var e,t="here isMy_phone_number";beforeEach(module("ui.inflector")),beforeEach(inject(function(t){e=t("inflector")})),describe("default",function(){it("should default to humanize",function(){expect(e(t)).toEqual("Here Is My Phone Number")}),it("should fail gracefully for invalid input",function(){expect(e(undefined)).toBeUndefined()}),it("should do nothing for empty input",function(){expect(e("")).toEqual("")})}),describe("humanize",function(){it("should uppercase first letter and separate words with a space",function(){expect(e(t,"humanize")).toEqual("Here Is My Phone Number")})}),describe("underscore",function(){it("should lowercase everything and separate words with an underscore",function(){expect(e(t,"underscore")).toEqual("here_is_my_phone_number")})}),describe("variable",function(){it("should remove all separators and camelHump the phrase",function(){expect(e(t,"variable")).toEqual("hereIsMyPhoneNumber")}),it("should do nothing if already formatted properly",function(){expect(e("hereIsMyPhoneNumber","variable")).toEqual("hereIsMyPhoneNumber")})})});