describe("uiAnimate",function(){var e,t,n,r=angular.module("ui.config");beforeEach(module("ui.directives")),beforeEach(inject(function(r,i,s){e=r,t=i,n=s})),afterEach(function(){r.value("ui.config",{})}),describe("behavior",function(){it("should add a ui-animate class when the dom is compiled",function(){var n=t("<div ui-animate></div>")(e);expect(n.hasClass("ui-animate")).toBeTruthy()}),it("should remove the ui-animate class immediately after injection",function(){var r=t("<div ui-animate></div>")(e);n.flush(),expect(r.hasClass("ui-animate")).toBeFalsy()})}),describe("options",function(){describe("passed",function(){it("should use a string as the class",function(){var n=t("<div ui-animate=\" 'ui-hide' \"></div>")(e);expect(n.hasClass("ui-hide")).toBeTruthy()}),it("should use an object's class attribute as the class",function(){var n=t("<div ui-animate=\" { 'class' : 'ui-hide' } \"></div>")(e);expect(n.hasClass("ui-hide")).toBeTruthy()})}),describe("global",function(){var n;beforeEach(inject(function(e){n=e.get("ui.config")})),it("should use a string as the class",function(){n.animate="ui-hide-global";var r=t("<div ui-animate></div>")(e);expect(r.hasClass("ui-hide-global")).toBeTruthy()}),it("should use an object's class attribute as the class",function(){n.animate={"class":"ui-hide-global"};var r=t("<div ui-animate></div>")(e);expect(r.hasClass("ui-hide-global")).toBeTruthy()})})})});