describe("uiCurrency",function(){var e;beforeEach(module("ui")),beforeEach(inject(function(t){e=t.$new()})),describe("use on a div element with two-way binding",function(){it("should have ui-currency-pos style non-zero positive model number ",function(){inject(function(t){var n;n=t("<div ui-currency ng-model='aNum'></div>")(e),e.$apply(function(){e.aNum=.5123}),expect(n.text()).toEqual("$0.51"),expect(n.hasClass("ui-currency-pos")).toBeTruthy(),expect(n.hasClass("ui-currency-neg")).toBeFalsy(),expect(n.hasClass("ui-currency-zero")).toBeFalsy()})}),it("should have ui-currency-neg style when negative model number",function(){inject(function(t){var n;n=t("<div ui-currency ng-model='aNum'></div>")(e),e.$apply(function(){e.aNum=-123}),expect(n.text()).toEqual("($123.00)"),expect(n.hasClass("ui-currency-pos")).toBeFalsy(),expect(n.hasClass("ui-currency-neg")).toBeTruthy()})}),it("should have ui-currency-zero style when zero model number",function(){inject(function(t){var n;n=t("<div ui-currency ng-model='aNum'></div>")(e),e.$apply(function(){e.aNum=0}),expect(n.text()).toEqual("$0.00"),expect(n.hasClass("ui-currency-pos")).toBeFalsy(),expect(n.hasClass("ui-currency-neg")).toBeFalsy(),expect(n.hasClass("ui-currency-zero")).toBeTruthy()})}),it("should not have any ui-currency styles or a value at all when missing scope model value",function(){inject(function(t){var n;n=t("<div ui-currency ng-model='aMissingNum'></div>")(e),expect(n.text()).toEqual(""),expect(n.hasClass("ui-currency-pos")).toBeFalsy(),expect(n.hasClass("ui-currency-neg")).toBeFalsy(),expect(n.hasClass("ui-currency-zero")).toBeFalsy()})}),it("should not have any ui-currency styles or a value at all when provided a non-numeric model value",function(){inject(function(t){var n;n=t("<div ui-currency ng-model='aBadNum'></div>")(e),e.$apply(function(){e.aBadNum="bad"}),expect(n.text()).toEqual(""),expect(n.hasClass("ui-currency-pos")).toBeFalsy(),expect(n.hasClass("ui-currency-neg")).toBeFalsy(),expect(n.hasClass("ui-currency-zero")).toBeFalsy()})}),it("should have user-defined positive style when provided in uiCurrency attr",function(){inject(function(t){var n;n=t("<div ui-currency=\"{ pos:'pstyle' }\" ng-model='aNum'></div>")(e),e.$apply(function(){e.aNum=1}),expect(n.hasClass("pstyle")).toBeTruthy()})})}),describe("use on a tag element",function(){it("should have a defined element",function(){inject(function(t){var n;n=t("<ui-currency ng-model='aNum'></ui-currency>")(e),e.$apply(function(){e.aNum=1}),expect(n).toBeDefined(),expect(n.text()).toEqual("$1.00")})})})});