describe("uiScrollfix",function(){var e,t,n;beforeEach(module("ui.directives")),beforeEach(inject(function(r,i,s){e=r.$new(),t=i,n=s})),describe("compiling this directive",function(){it('should bind to window "scroll" event with a ui-scrollfix namespace',function(){spyOn($.fn,"on"),t('<div ui-scrollfix="100"></div>')(e),expect($.fn.on).toHaveBeenCalled(),expect($.fn.on.mostRecentCall.args[0]).toBe("scroll.ui-scrollfix")})}),describe("scrolling the window",function(){it("should add the ui-scrollfix class if the offset is greater than specified",function(){var r=t('<div ui-scrollfix="-100"></div>')(e);$(n).trigger("scroll.ui-scrollfix"),expect(r.hasClass("ui-scrollfix")).toBe(!0)}),it("should remove the ui-scrollfix class if the offset is less than specified (using absolute coord)",function(){var r=t('<div ui-scrollfix="100" class="ui-scrollfix"></div>')(e);$(n).trigger("scroll.ui-scrollfix"),expect(r.hasClass("ui-scrollfix")).toBe(!1)}),it("should remove the ui-scrollfix class if the offset is less than specified (using relative coord)",function(){var r=t('<div ui-scrollfix="+100" class="ui-scrollfix"></div>')(e);$(n).trigger("scroll.ui-scrollfix"),expect(r.hasClass("ui-scrollfix")).toBe(!1)})})});