describe("uiSortable",function(){beforeEach(module("ui.directives")),describe("simple use",function(){it("should have a ui-sortable class",function(){inject(function(e,t){var n;n=e("<ul ui-sortable></ul>")(t),expect(n.hasClass("ui-sortable")).toBeTruthy()})}),it("should update model when order changes",function(){inject(function(e,t){var n;n=e('<ul ui-sortable ng-model="items"><li ng-repeat="item in items" id="s-{{$index}}">{{ item }}</li></ul>')(t),t.$apply(function(){return t.items=["One","Two","Three"]}),n.find("li:eq(1)").insertAfter(n.find("li:eq(2)"))})})})});